mapper.sql:
FROM ${baseTablePrefix}permission A

    A.DISPLAY = TRUE

        AND A.ID = #{id}


        AND A.PID = #{pid}


        AND A.CODE = #{code}


        AND A.CODE like concat(#{codeLike}, '%') escape '/'


        AND A.OPERATION like concat('%', concat(#{operationLike}, '%')) escape '/'


        AND A.ROUTE = #{route}


        AND A.URL = #{url}



            AND A.MENU = TRUE


            AND A.MENU = FALSE

mapper.update:
UPDATE ${baseTablePrefix}permission
SET DISPLAY = FALSE
WHERE ID = #{id}
